import React from 'react'
import styled from 'styled-components'

import Container from './container'

const Section = styled.div`
    width: 100%;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 56px 32px;
    background-color: ${p => p.theme.colors.N0};
    @media screen and (min-width: 768px) {
        padding: 125px 0px;
    }
`
const Row = styled.div`
    width: 100%;
    display: grid;
    grid-template-columns: repeat(1, 1fr);
    grid-auto-rows: minmax(100px, auto);
    border-top: 1px solid ${p => p.theme.colors.N500};
    border-left: 1px solid ${p => p.theme.colors.N500};
    @media screen and (min-width: 768px) {
        grid-template-columns: repeat(3, 1fr);
        grid-auto-rows: minmax(100px, auto);
    }
`
const Column = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;
    border-bottom: 1px solid ${p => p.theme.colors.N500};
    border-right: 1px solid ${p => p.theme.colors.N500};
    box-sizing: border-box;
    padding: 40px;
    @media screen and (min-width: 768px) {
        width: 100%;
    }
`
const Content = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;
    @media screen and (min-width: 768px) {
        width: 100%;
    }
`
const Title = styled.h2`
    color: ${p => p.theme.colors.N700};
    font-size: 24px;
    font-weight: 600;
    margin: 0 0 20px;
    padding: 0;
    @media screen and (min-width: 768px) {
        text-align: left;
        font-size: 24px;
        width: 100%;
    }
`
const Description = styled.p`
    font-size: 16px;
    line-height: 24px;
    color: ${p => p.theme.colors.N600};
    font-weight: 400;
    margin-bottom: 0px;
    @media screen and (min-width: 768px) {
        text-align: left;
    }
`
const Brands = styled.div`
    width: 100%;
    display: flex;
    flex-direction: row;
    margin: 8px 0px 32px;
    svg {
        margin-right: 20px;
    }
    @media screen and (min-width: 768px) {
        width: 100%;
    }
`
